INSERT INTO `resumens` VALUES (1,'Resumen del Programa de Estudio de Contabilidad','<h3>La Carrera</h3>\n\n<p>Contabilidad forma al profesional integral, capacitado para gerenciar organizaciones privadas y p&uacute;blicas seg&uacute;n las competencias que se detalla m&aacute;s adelante.</p>\n\n<h3>Formaci&oacute;n</h3>\n\n<p>OBJETIVOS EDUCACIONALES:</p>\n\n<ul>\n	<li>Alcanzar s&oacute;lida formaci&oacute;n profesional con fundamentos filos&oacute;ficos, cient&iacute;ficos y socioculturales respetando la diversidad.</li>\n	<li>Formar profesionales proactivos en Ciencias Contables y Financieras para la sociedad.</li>\n	<li>Promover y realizar investigaci&oacute;n cient&iacute;fica, tecnol&oacute;gica y human&iacute;stica.</li>\n	<li>Proyectar a la comunidad acciones y servicios orientados al desarrollo econ&oacute;mico y social.</li>\n	<li>Promover el desarrollo humano y sostenible en el &aacute;mbito local, regional, nacional y mundial.</li>\n	<li style=\"text-align: justify;\">Conocer y aplicar los curr&iacute;culos, niveles en los procesos de ense&ntilde;anza y aprendizaje, valorando la dimensi&oacute;n humana del estudiante desde una perspectiva intercultural.</li>\n</ul>',NULL,1,0,1,'2021-11-29 05:17:06','2021-12-07 05:30:55',NULL,1,2);
/*!40000 ALTER TABLE `resumens` ENABLE KEYS */;
